void GHardFont_GetCharCoords(char c, int* pX, int* pY, int* pWidth, int* pHeight)
{
	unsigned char index = (unsigned char)c - 32;
	if(index > 95)
		index = 95;
	*pX = hard_font_offsets[index];
	*pY = 0;
	*pWidth = hard_font_offsets[index + 1] - *pX;
	*pHeight = 33;
}

void GSmallHardFont_GetCharCoords(char c, int* pX, int* pY, int* pWidth, int* pHeight)
{
	unsigned char index = (unsigned char)c - 32;
	if(index > 95)
		index = 95;
	*pX = small_hard_font_offsets[index];
	*pY = 0;
	*pWidth = small_hard_font_offsets[index + 1] - *pX;
	*pHeight = 12;
}

inline unsigned int GHardFont_GetPixel(int x, int y)
{
	return (((hard_font_pixels[y])[x] - 32) << 2);
}

inline unsigned int GSmallHardFont_GetPixel(int x, int y)
{
	return (((small_hard_font_pixels[y])[x] - 32) << 2);
}

unsigned int GHardFont_GetWhiteOnBlack(int x, int y)
{
	unsigned int n = GHardFont_GetPixel(x, y);
	return (n | (n << 8) | (n << 16));
}

unsigned int GHardFont_GetBlackOnWhite(int x, int y)
{
	unsigned int n = (~(GHardFont_GetPixel(x, y))) & 255;
	return (n | (n << 8) | (n << 16));
}

unsigned int GHardFont_GetAlphaBlended(int x, int y, unsigned int nColor, unsigned int nBackground)
{
	unsigned int n = GHardFont_GetPixel(x, y);
	int r = n * (nColor & 255) + (255 - n) * (nBackground & 255);
	int g = n * ((nColor >> 8) & 255) + (255 - n) * ((nBackground >> 8) & 255);
	int b = n * ((nColor >> 16) & 255) + (255 - n) * ((nBackground >> 16) & 255);
	return ((r / 255) | ((g / 255) << 8) | ((b / 255) << 16));
}

unsigned int GSmallHardFont_GetAlphaBlended(int x, int y, unsigned int nColor, unsigned int nBackground)
{
	unsigned int n = GSmallHardFont_GetPixel(x, y);
	int r = n * (nColor & 255) + (255 - n) * (nBackground & 255);
	int g = n * ((nColor >> 8) & 255) + (255 - n) * ((nBackground >> 8) & 255);
	int b = n * ((nColor >> 16) & 255) + (255 - n) * ((nBackground >> 16) & 255);
	return ((r / 255) | ((g / 255) << 8) | ((b / 255) << 16));
}

